// @ts-check

const { join, resolve } = require('path');
const { fetchBinariesFromPackage, getBranchName } = require('@skype/ivy-downloader');

fetchBinariesFromPackage({
    name: 'cld',
    branch: getBranchName({ name: 'cld', electronBranch: 'skype-3.0' }),
    revision: '2.4.6.2',
    binaries: [join('build', 'Release', 'cld.node')]
}, resolve(__dirname));
