// @ts-check

const { join, resolve } = require('path');
const { fetchBinariesFromPackage, getBranchName } = require('@skype/ivy-downloader');

fetchBinariesFromPackage({
    name: 'spellchecker',
    branch: getBranchName({ name: 'spellchecker', electronBranch: 'skype-3.0' }),
    revision: '4.0.6.2',
    binaries: [join('build', 'Release', 'spellchecker.node')]
}, resolve(__dirname));
