// @ts-check

const { join, resolve } = require('path');
const { fetchBinariesFromPackage, getBranchName } = require('@skype/ivy-downloader');

fetchBinariesFromPackage({
    name: 'keyboard-layout',
    branch: getBranchName({ name: 'keyboard-layout', electronBranch: 'skype-3.0' }),
    revision: '2.0.13.4',
    binaries: [join('build', 'Release', 'keyboard-layout-manager.node')]
}, resolve(__dirname));
