// @ts-check

const { join, resolve } = require('path');
const { fetchBinariesFromPackage, getBranchName } = require('@skype/ivy-downloader');

fetchBinariesFromPackage({
    name: 'keytar',
    branch: getBranchName({ name: 'keytar', electronBranch: 'skype-3.0' }),
    revision: '4.0.2.2',
    binaries: [join('build', 'Release', 'keytar.node')]
}, resolve(__dirname));
