// @ts-check

const { join, resolve } = require('path');
const { fetchBinariesFromPackage, getBranchName } = require('@skype/ivy-downloader');

fetchBinariesFromPackage({
    name: 'sqlite3',
    branch: getBranchName({ name: 'sqlite3', electronBranch: 'skype-3.0' }),
    revision: '3.1.13.2',
    binaries: [join('lib', 'binding', 'node_sqlite3.node')]
}, resolve(__dirname));
